    public static ArrayList<Book> getListData() {
        ArrayList<Book> list = new ArrayList<>();
        for (String[] datane : data) {
            Book book = new Book();
            book.setTitle(datane[0]);
            book.setDesc(datane[1]);
            book.setWriter(datane[2]);
            book.setPublisher(datane[3]);
            book.setYear(datane[4]);
            book.setGenre(datane[5]);
            book.setImage(datane[6]);
            list.add(book);
        }
        return list;
    }
